/*This SSB job will create a summary view of IPDR usage as tumbling window aggregation over the past 1 hour upon data arrivale 
grouped by window_start, window_end, MacAddress and DataType */
/*This SSB job will create a summary view of IPDR usage as tumbling window aggregation over the past 1 hour upon data arrivale grouped by Mac Address */
DROP VIEW IF EXISTS `ssb`.`IPDRStreaming`.`ipdr_summary`;
CREATE VIEW `ssb`.`IPDRStreaming`.`ipdr_summary` AS
SELECT window_start, window_end, cmMacAddr, dsScn, SUM(dsOctets)  as Usage
FROM TABLE(TUMBLE(TABLE ipdr_table, DESCRIPTOR(eventTimestamp), INTERVAL '60' MINUTE))
WHERE dsScn = 'data'
GROUP BY window_start,window_end,cmMacAddr, dsScn;

DESCRIBE `ssb`.`IPDRStreaming`.`ipdr_summary`;
