/* INSERT INTO statement for Hive table hourly_roleup */
INSERT INTO `Hive`.`default`.`hourly_roleup`
SELECT window_start, window_end, cmMacAddr, SUM(usOctets) AS sum1, SUM(dsOctets) AS sum2 
FROM TABLE( TUMBLE(TABLE ipdr_table, DESCRIPTOR(eventTimestamp), INTERVAL '30' SECONDS)) 
GROUP BY window_start, window_end, ROLLUP(cmMacAddr);
